import React, { useEffect, useState } from 'react';
import axios from 'axios';
import '../styles/MyProfile.css';

const MyProfile = () => {
  const [userData, setUserData] = useState({});
  const [courseCount, setCourseCount] = useState(0);

  const token = localStorage.getItem('token');
  const userId = localStorage.getItem('userId');
  const role = localStorage.getItem('role');

  useEffect(() => {
    // Fetch user details from backend
    const fetchUserDetails = async () => {
      try {
        const response = await axios.get(`http://localhost:8080/api/auth/profile/${userId}`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setUserData(response.data);
      } catch (error) {
        console.error('Error fetching user profile:', error);
      }
    };

    // Fetch course count based on role
    const fetchCourseCount = async () => {
      try {
        if (role === 'ROLE_ADMIN') {
          const res = await axios.get(`http://localhost:8080/courses/byInstructor/${userId}`);
          setCourseCount(res.data.length);
        } else if (role === 'ROLE_USER') {
          const res = await axios.get(`http://localhost:8080/enrollments/user/${userId}`);
          setCourseCount(res.data.length);
        }
      } catch (error) {
        console.error('Error fetching course data:', error);
      }
    };

    fetchUserDetails();
    fetchCourseCount();
  }, [userId, role, token]);

  return (
    <div style={{ padding: '2rem' }}>
      <h2>My Profile</h2>
      <p><strong>Username:</strong> {userData.userName}</p>
      <p><strong>Email:</strong> {userData.email}</p>
      <p><strong>Role:</strong> {role.replace('ROLE_', '')}</p>

      {role === 'ROLE_ADMIN' && (
        <p><strong>Courses Created:</strong> {courseCount}</p>
      )}

      {role === 'ROLE_USER' && (
        <p><strong>Courses Enrolled:</strong> {courseCount}</p>
      )}
    </div>
  );
};

export default MyProfile;
