import React, { useEffect, useState } from 'react';
import AdminHeader from './AdminHeader';
import '../styles/AdminCourses.css';
import { toast } from 'react-toastify';

const AdminCourses = () => {
  const [courses, setCourses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [editCourse, setEditCourse] = useState(null);

  const userId = localStorage.getItem('userId');
  const token = localStorage.getItem('token');

  const fetchCourses = () => {
    setLoading(true);
    fetch(`http://localhost:8080/courses/createdBy/${userId}`, {
      method: 'GET',
      headers: {
        'Authorization': `Bearer ${token}`,
        'Content-Type': 'application/json',
      },
    })
      .then((res) => {
        if (!res.ok) throw new Error(`Error: ${res.status}`);
        return res.json();
      })
      .then((data) => {
        setCourses(data);
        setLoading(false);
      })
      .catch((err) => {
        setError(err.message);
        setLoading(false);
      });
  };

  const handleDelete = (courseId) => {
    fetch(`http://localhost:8080/courses/${courseId}`, {
      method: 'DELETE',
      headers: {
        'Authorization': `Bearer ${token}`,
        'Content-Type': 'application/json',
      },
    })
      .then((res) => {
        if (!res.ok) throw new Error(`Error deleting: ${res.status}`);
        toast.success("Course deleted successfully!");
        fetchCourses();
      })
      .catch(() => toast.error("Failed to delete course"));
  };

  const handleEditClick = (course) => {
    setEditCourse({ ...course }); // open form with course details
  };

  const handleUpdateChange = (e) => {
    const { name, value } = e.target;
    setEditCourse({ ...editCourse, [name]: value });
  };

  const handleUpdateSubmit = (e) => {
    e.preventDefault();

    fetch(`http://localhost:8080/courses/${editCourse.courseId}`, {
      method: 'PUT',
      headers: {
        'Authorization': `Bearer ${token}`,
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(editCourse),
    })
      .then((res) => {
        if (!res.ok) throw new Error(`Update failed: ${res.status}`);
        toast.success("Course updated successfully!");
        setEditCourse(null);
        fetchCourses();
      })
      .catch(() => toast.error("Failed to update course"));
  };

  useEffect(() => {
    if (!userId || !token) {
      toast.error('Please log in as admin to view your courses');
      setLoading(false);
      return;
    }
    fetchCourses();
  }, [userId, token]);

  return (
    <>
      <AdminHeader />
      <div className="admin-course-container">
        <h2 className="admin-course-heading">Courses Created By You</h2>

        {loading ? (
          <p className="admin-course-loading">Loading courses...</p>
        ) : error ? (
          <p className="admin-course-error">Error: {error}</p>
        ) : courses.length === 0 ? (
          <p className="admin-course-loading">You haven't created any courses yet.</p>
        ) : (
          <div className="admin-course-grid">
            {courses.map((course) => (
              <div className="admin-course-card" key={course.courseId}>
                <h3>{course.title}</h3>
                <p><strong>Duration:</strong> {course.durationInHours} hrs</p>
                <p><strong>Price:</strong> ₹{course.coursePrice}</p>
                <p><strong>Description:</strong> {course.description}</p>
                <button
                  className="delete-course-button"
                  onClick={() => handleDelete(course.courseId)}
                >
                  Delete
                </button>
                <button
                  className="update-course-button"
                  onClick={() => handleEditClick(course)}
                >
                  Update
                </button>
              </div>
            ))}
          </div>
        )}

        {editCourse && (
          <div className="edit-course-modal">
            <h3>Edit Course</h3>
            <form onSubmit={handleUpdateSubmit} className="edit-course-form">
              <input
                type="text"
                name="title"
                value={editCourse.title}
                onChange={handleUpdateChange}
                placeholder="Course Title"
                required
              />
              <textarea
                name="description"
                value={editCourse.description}
                onChange={handleUpdateChange}
                placeholder="Description"
                required
              />
              <input
                type="number"
                name="durationInHours"
                value={editCourse.durationInHours}
                onChange={handleUpdateChange}
                placeholder="Duration in Hours"
                required
              />
              <input
                type="number"
                name="coursePrice"
                value={editCourse.coursePrice}
                onChange={handleUpdateChange}
                placeholder="Course Price"
                required
              />
              <input
                type="text"
                name="instructorName"
                value={editCourse.instructorName}
                onChange={handleUpdateChange}
                placeholder="Instructor Name"
                required
              />
              <button type="submit" className="save-button">Save</button>
              <button
                type="button"
                className="cancel-button"
                onClick={() => setEditCourse(null)}
              >
                Cancel
              </button>
            </form>
          </div>
        )}
      </div>
    </>
  );
};

export default AdminCourses;
