import React, { useEffect, useState } from 'react';
import Header from './Header';
import { toast } from 'react-toastify';

const MyCourses = () => {
  const [enrolledCourses, setEnrolledCourses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    // Get user ID from localStorage
    const userId = localStorage.getItem('userId');

    if (!userId) {
      toast.error('Please login to view your courses');
      return;
    }

    fetch(`http://localhost:8080/enrollments/${userId}`)
      .then(response => {
        if (!response.ok) {
          throw new Error(`Error: ${response.status}`);
        }
        return response.json();
      })
      .then(data => {
        setEnrolledCourses(data);
        setLoading(false);
      })
      .catch(err => {
        console.error('Fetch error:', err);
        setError(err.message);
        setLoading(false);
      });
  }, []);

  return (
    <>
      <Header />
      <div className="course-container">
        <h2 className="course-heading">My Enrolled Courses</h2>

        {loading ? (
          <p className="course-loading">Loading your courses...</p>
        ) : error ? (
          <p className="course-error">Error: {error}</p>
        ) : enrolledCourses.length === 0 ? (
          <p className="course-empty">You haven't enrolled in any courses yet.</p>
        ) : (
          <div className="course-grid">
            {enrolledCourses.map(course => (
              <div className="course-card" key={course.courseId}>
                <h3>{course.title}</h3>
                <p><strong>Instructor:</strong> {course.instructorName}</p>
                <p><strong>Duration:</strong> {course.durationInHours} hrs</p>
                <p><strong>Enrollment Date:</strong> {new Date(course.enrollmentDate).toLocaleDateString()}</p>
              </div>
            ))}
          </div>
        )}
      </div>
    </>
  );
};

export default MyCourses;
