import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/HomePage.css';

const HomePage = () => {
  const [employees, setEmployees] = useState([]);
  const [editingId, setEditingId] = useState(null);
  const [editData, setEditData] = useState({ name: '', job: '' });
  const [displayEmployee, setDisplayEmployee] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [showEmployeeList, setShowEmployeeList] = useState(false);

  const navigate = useNavigate();
  const backendUrl = 'http://localhost:8080';

  // Fetch all employees
  const handleShowEmployees = () => {
    setShowEmployeeList(true);
    setLoading(true);
    setError(null);
    fetch(`${backendUrl}/employee`)
      .then((res) => {
        if (!res.ok) {
          throw new Error(`Status: ${res.status}`);
        }
        return res.json();
      })
      .then((data) => {
        setEmployees(data);
        setLoading(false);
      })
      .catch((err) => {
        setError(err.message);
        setLoading(false);
      });
  };

  // Update employee
  const handleUpdateEmployee = (empID) => {
    fetch(`${backendUrl}/employee/${empID}`, {
      method: 'PUT',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(editData),
    })
      .then((res) => {
        if (!res.ok) throw new Error(`Failed to update. Status: ${res.status}`);
        setEmployees((prev) =>
          prev.map((emp) => (emp.empID === empID ? { ...emp, ...editData } : emp))
        );
        setEditingId(null);
        setEditData({ name: '', job: '' });
      })
      .catch((err) => alert(err.message));
  };

  // Delete employee
  const handleDeleteEmployee = (empID, name) => {
    if (!window.confirm(`Delete employee ${name}?`)) return;
    fetch(`${backendUrl}/employee/${empID}`, { method: 'DELETE' })
      .then((res) => {
        if (!res.ok) throw new Error(`Failed to delete. Status: ${res.status}`);
        setEmployees((prev) => prev.filter((emp) => emp.empID !== empID));
      })
      .catch((err) => alert(err.message));
  };

  const startEdit = (emp) => {
    setEditingId(emp.empID);
    setEditData({ name: emp.name, job: emp.job });
  };

  const cancelEdit = () => {
    setEditingId(null);
    setEditData({ name: '', job: '' });
  };

  const handleDisplayEmployee = (emp) => setDisplayEmployee(emp);
  const closeDisplayEmployee = () => setDisplayEmployee(null);

  return (
    <div className="landing-container">
      <div className="landing-content">
        <h1>Welcome!</h1>
        <p>You are in the Employee Management System.</p>
        <button onClick={handleShowEmployees} disabled={loading}>Show Employees</button>
        <button onClick={() => navigate('/add-employee')}>Add Employee</button>

        {showEmployeeList && (
          <>
            <h2>Employees</h2>
            {loading && <p>Loading...</p>}
            {error && <p style={{ color: 'red' }}>Error: {error}</p>}
            {!loading && employees.length === 0 && <p>No employees found.</p>}
            {!loading && employees.length > 0 && (
              <table className="employee-table">
                <thead>
                  <tr>
                    <th>ID</th>
                    <th>Name</th>
                    <th>Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {employees.map((emp) => (
                    <tr key={emp.empID}>
                      <td>{emp.empID}</td>
                      <td>
                        {editingId === emp.empID ? (
                          <input
                            value={editData.name}
                            onChange={(e) => setEditData({ ...editData, name: e.target.value })}
                          />
                        ) : (
                          emp.name
                        )}
                      </td>
                      <td>
                        {editingId === emp.empID ? (
                          <>
                            <input
                              value={editData.job}
                              onChange={(e) => setEditData({ ...editData, job: e.target.value })}
                              placeholder="Job"
                            />
                            <button onClick={() => handleUpdateEmployee(emp.empID)}>Save</button>
                            <button onClick={cancelEdit}>Cancel</button>
                          </>
                        ) : (
                          <>
                            <button onClick={() => startEdit(emp)}>Update</button>
                            <button onClick={() => handleDeleteEmployee(emp.empID, emp.name)}>Delete</button>
                            <button onClick={() => handleDisplayEmployee(emp)}>Display</button>
                          </>
                        )}
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            )}
          </>
        )}

        {displayEmployee && (
          <div className="employee-details">
            <h3>Employee Details</h3>
            <p><strong>ID:</strong> {displayEmployee.empID}</p>
            <p><strong>Name:</strong> {displayEmployee.name}</p>
            <p><strong>Job:</strong> {displayEmployee.job}</p>
            <button onClick={closeDisplayEmployee}>Close</button>
          </div>
        )}
      </div>
    </div>
  );
};

export default HomePage;
